<?php
$input = str_split("");
$protocol = array();
$found = 0;
$c=0;
while($c < count($input)){
    $j=0;
    for($i=$c; $i<$c+4; $i++) {
        $protocol[$j] = $input[$i];
        if(count($protocol)==4){
            print_r($protocol);
            echo " - ".count(array_unique($protocol))." - ";
            if (count(array_unique($protocol))<4){
                unset($protocol);
            } else {
                $line = $c+3;
                echo " Unique!".$line;
                unset($protocol);
                $found = 1;
                break;
            }
            echo "<br>";
        }
        $j++;
        if ($j>3) $j-=4;
    }
    if ($found==1) { break; }
    $c++;
}
//echo $c;
?>

